  validateConfig() {
    const required = ['clientId', 'clientSecret', 'redirectUri', 'authorizationEndpoint', 'tokenEndpoint'];

    for (const field of required) {
      if (!this.config[field]) {
        throw new Error(`Missing required OAuth configuration: ${field}`);
      }
    }
  }

